def isin(arr1: list, arr2: list) -> bool:
    for i in range(len(arr2) - len(arr1) + 1):
        if arr1 == arr2[i:i+len(arr1)]:
            return True
    return False

def findLength(nums1: list, nums2: list) -> int:
    if nums1 == nums2:
        return len(nums1)
    for size in reversed(range(len(nums1))):
            for start in range(len(nums1)-(size)):
                arr1 = nums1[start:start+size+1]
                arr2 = nums2
                if isin(arr1, arr2):
                    return size + 1
    return 0

